import React, { useEffect,useState } from 'react';
import {Link} from 'react-router-dom';
import {useDispatch, useSelector} from 'react-redux';
// import data from '../data';
import { cleanup } from 'axe-core';
import { detailsProduct } from '../actions/productActions';

function ProductScreen (props){
    // console.log(props.match.params.id);
    // const product = data.products.find(x => x._id === props.match.params.id);
    const [qty,setQty] = useState(1);
    const productDetails = useSelector(state=>state.productDetails);
    const {product , loading ,error } = productDetails;
    const dispatch = useDispatch();

    useEffect(()=>{
        dispatch(detailsProduct(props.match.params.id));
        return () =>{
            //
        };
    },[]);

    return <div >

        <div className="back-to-result"><Link to="/">Back </Link></div>  
        {loading? <div>Loading....</div>:
         error ? <div>{error} </div>:
         (
            <div className="details">
        <div className="details-image">
            <img src={product.image} alt="product "></img>
        </div>
        <div className="details-info">
            <ul>
                <li><h4>{product.name}</h4></li>
                <li><h4>{product.brand}</h4></li>
                <li><b>Price : {product.price} INR</b></li>
                <li>
                    Description:
                    <div><h4>{product.description}</h4></div>

                </li>
            </ul>
        </div>
        <div className="details-action">
            <ul>
                <li>
                    Price: {product.price}

                </li>
                <li>
                    Quantity: <select value={qty} onChange={(e)=>{ setQty(e.type.value)}}>
                        <option>1</option>
                        <option>2</option>
                        <option>3</option>
                        <option>4</option>
                        <option>5</option>
                        <option>6</option>
                    </select>
                </li>
                <li>
                    <button className="button">Add to Cart</button>
                </li>
            </ul>
        </div>
        </div>
         )
        }

    </div>
}

export default ProductScreen;
