import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { Link } from 'react-router-dom';
import { addToCart } from '../actions/cartActions';

function CartScreen(props){

    const cart = useSelector(state=>state.cart);

    const {cartItems} = cart;

    const productId = props.match.params.id;
    const qty = props.location.search? Number(props.location.search.split("=")[1]):1;
    const dispatch = useDispatch();
    useEffect(()=>{
        if(productId){
            dispatch(addToCart(productId,qty));
        }
    },[])

    return <div className="cart">
        <div className="cart-list">
            <ul className="cart-list-container">
                <li>
                    <h3>Shopping Cart</h3>

                {/* <div>
                    Price
                </div> */}
                </li>

                    {
                        cartItems.length==0?
                        <div>
                            Empty Cart!
                        </div>
                        :
                        cartItems.map( item=>
                            <li>
                            <div className="cart-image">
                                <img src={item.image} alt="image" /> 
                            <div className="cart-name">
                                <div className="cart-name"> 
                                <Link to={"/product/" + item.product}>
                                {item.name}
                                </Link>

                                </div>
                                <div>
                                    Qty:
                                    <select>
                                        <option valu="1">1</option>
                                        <option valu="2">2</option>
                                        <option valu="3">3</option>
                                    </select>
                                    <button type="button" className="button" onClick={removeFromCartHandler}>
                                        Delete
                                    </button>
                                </div>
                            </div>
                            <div className="cart-price">
                                Price: {item.price } INR
                            </div>
                            </div>
                            </li>
                        )

                    }

            </ul>
        </div>
        <div className="cart-action">
                    <h3>Subtotal ( {cartItems.reduce((a,c)=> a + c.qty,0)} items)

                    :
                    INR {cartItems.reduce((a,c) => a + c.qty*c.price,0)}

                    </h3> 
                    <button className="button" disabled={cartItems.length=== 0}>
                        Checkout
                    </button>
        </div>

    </div>
}

export default CartScreen;

